                  onClick={() => togglePageSelection(page.pageNumber)}
                  data-testid={`page-${page.pageNumber}`}
                >
                  <div className="flex flex-col items-center">
                    <i className="fas fa-file-alt text-2xl mb-2 text-gray-400"></i>
