import { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';

export default function CaseConverter() {
  const [inputText, setInputText] = useState('');
  const [results, setResults] = useState({});
  const { toast } = useToast();

  const convertCases = useCallback((text) => {
    if (!text.trim()) {
      setResults({});
      return;
    }

    const conversions = {
      uppercase: text.toUpperCase(),
      lowercase: text.toLowerCase(),
      titleCase: text.replace(/\w\S*/g, (txt) => 
        txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
      ),
      sentenceCase: text.charAt(0).toUpperCase() + text.slice(1).toLowerCase(),
      camelCase: text
        .replace(/(?:^\w|[A-Z]|\b\w)/g, (word, index) => 
          index === 0 ? word.toLowerCase() : word.toUpperCase()
        )
        .replace(/\s+/g, ''),
      pascalCase: text
        .replace(/(?:^\w|[A-Z]|\b\w)/g, (word) => word.toUpperCase())
        .replace(/\s+/g, ''),
      snakeCase: text
        .trim()
        .toLowerCase()
        .replace(/\s+/g, '_')
        .replace(/[^\w_]/g, ''),
      kebabCase: text
        .trim()
        .toLowerCase()
        .replace(/\s+/g, '-')
        .replace(/[^\w-]/g, ''),
      constantCase: text
        .trim()
        .toUpperCase()
        .replace(/\s+/g, '_')
        .replace(/[^\w_]/g, ''),
      dotCase: text
        .trim()
        .toLowerCase()
        .replace(/\s+/g, '.')
        .replace(/[^\w.]/g, ''),
      pathCase: text
        .trim()
        .toLowerCase()
        .replace(/\s+/g, '/')
        .replace(/[^\w/]/g, ''),
      alternatingCase: text
        .split('')
        .map((char, index) => 
          index % 2 === 0 ? char.toLowerCase() : char.toUpperCase()
        )
        .join(''),
      inverseCase: text
        .split('')
        .map(char => 
          char === char.toUpperCase() ? char.toLowerCase() : char.toUpperCase()
        )
        .join(''),
      capitalizeWords: text.replace(/\b\w/g, char => char.toUpperCase())
    };

    setResults(conversions);
  }, []);

  const handleInputChange = (e) => {
    const text = e.target.value;
    setInputText(text);
    convertCases(text);
  };

  const copyToClipboard = async (text, caseName) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copied!",
        description: `${caseName} copied to clipboard`,
        duration: 2000,
