          </div>
        )}

        {/* Results */}
        {processedUrl && (
          <div className="glassmorphism rounded-xl p-6">
            <h3 className="text-xl font-semibold text-slate-100 mb-4">Enhanced Audio</h3>

            <div className="space-y-4">
