              <strong>Quantifiers:</strong>
              <br />* (0+), + (1+), ? (0-1), {'{n}'} (exactly n)
            </div>
            <div>
              <strong>Anchors:</strong>
              <br />^ (start), $ (end), \b (word boundary)
            </div>
            <div>
              <strong>Flags:</strong>
              <br />g (global), i (ignore case), m (multiline)
            </div>
          </div>
        </div>
      </div>
    </ToolShell>
  );
};

export default RegexTester;

