              <div className="flex space-x-2">
                <button
                  onClick={exportData}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm"
                  data-testid="button-export"
                >
                  <i className="fas fa-download mr-2"></i>
                  Export
