import { useState } from 'react';
import { motion } from 'framer-motion';
import ToolShell from '../../../components/Tools/ToolShell';
import { TOOLS } from '../../../lib/toolsIndex';

export default function JSONFormatter() {
  const tool = TOOLS.text.find(t => t.slug === 'json-formatter');
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [mode, setMode] = useState('format'); // 'format', 'minify', 'validate'
  const [error, setError] = useState('');
  const [isValid, setIsValid] = useState(null);

  const formatJSON = () => {
    try {
      setError('');
      const parsed = JSON.parse(inputText);
      const formatted = JSON.stringify(parsed, null, 2);
      setOutputText(formatted);
      setIsValid(true);
    } catch (err) {
      setError(`Invalid JSON: ${err.message}`);
      setOutputText('');
      setIsValid(false);
    }
  };

  const minifyJSON = () => {
    try {
      setError('');
      const parsed = JSON.parse(inputText);
      const minified = JSON.stringify(parsed);
      setOutputText(minified);
      setIsValid(true);
    } catch (err) {
      setError(`Invalid JSON: ${err.message}`);
      setOutputText('');
      setIsValid(false);
    }
  };

  const validateJSON = () => {
    try {
      setError('');
      JSON.parse(inputText);
      setIsValid(true);
      setOutputText('âœ… Valid JSON');
    } catch (err) {
      setError(`Invalid JSON: ${err.message}`);
      setOutputText('âŒ Invalid JSON');
      setIsValid(false);
    }
  };

  const handleProcess = () => {
    if (!inputText.trim()) {
      setError('Please enter some JSON to process.');
      return;
    }

    switch (mode) {
      case 'format':
        formatJSON();
        break;
      case 'minify':
        minifyJSON();
        break;
      case 'validate':
        validateJSON();
        break;
      default:
        formatJSON();
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(outputText);
  };

  const clearAll = () => {
    setInputText('');
    setOutputText('');
    setError('');
    setIsValid(null);
  };

  const loadSampleJSON = () => {
    const sample = {
      "name": "John Doe",
      "age": 30,
