    { key: 'savings', label: 'Savings & Investments', icon: 'fas fa-piggy-bank', recommended: 20 },
    { key: 'other', label: 'Other Expenses', icon: 'fas fa-ellipsis-h', recommended: 2 }
  ];

  const formatCurrency = (amount) => {
