import { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import ToolShell from '../../../components/Tools/ToolShell';
import { getToolBySlug } from '../../../lib/toolsIndex';

export default function BackgroundRemover() {
  const tool = getToolBySlug('image', 'remove-background');
  const [originalImage, setOriginalImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const fileInputRef = useRef(null);
  const canvasRef = useRef(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      alert('Please select a valid image file.');
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      setOriginalImage({
        file,
        dataUrl: e.target.result,
        name: file.name,
        size: file.size
      });
      setProcessedImage(null);
    };
    reader.readAsDataURL(file);
  };

  const removeBackground = async () => {
    if (!originalImage) return;

    setIsProcessing(true);

    try {
      // This is a simplified background removal simulation
      // In a real implementation, you would use libraries like:
      // - @mediapipe/selfie_segmentation for person detection
      // - TensorFlow.js with pre-trained models
      // - Canvas API for pixel manipulation

      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      // Load the image
      const img = new Image();
      img.onload = () => {
        canvas.width = img.width;
        canvas.height = img.height;

        // Draw the original image
        ctx.drawImage(img, 0, 0);

        // Get image data for processing
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
        const data = imageData.data;

        // Simple background removal algorithm (this is very basic)
        // In reality, you'd use ML models for accurate segmentation
        for (let i = 0; i < data.length; i += 4) {
          const red = data[i];
          const green = data[i + 1];
          const blue = data[i + 2];

          // Simple color-based background removal (remove white-ish backgrounds)
          // This is just for demonstration - real background removal requires ML
          if (red > 200 && green > 200 && blue > 200) {
            data[i + 3] = 0; // Set alpha to 0 (transparent)
          }
        }

        // Put the processed image data back
        ctx.putImageData(imageData, 0, 0);

        // Convert canvas to blob
        canvas.toBlob((blob) => {
          const url = URL.createObjectURL(blob);
          setProcessedImage({
            blob,
            dataUrl: url,
            name: `${originalImage.name.split('.')[0]}_no_bg.png`,
            size: blob.size
          });
          setIsProcessing(false);
        }, 'image/png');
      };

      img.src = originalImage.dataUrl;

    } catch (error) {
      console.error('Error removing background:', error);
      alert('Error processing image. Please try again.');
      setIsProcessing(false);
    }
  };

  const downloadImage = () => {
    if (!processedImage) return;

    const a = document.createElement('a');
    a.href = processedImage.dataUrl;
    a.download = processedImage.name;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const resetTool = () => {
    setOriginalImage(null);
    setProcessedImage(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <ToolShell tool={tool} category="image">
      <canvas ref={canvasRef} className="hidden" />

      <div className="grid lg:grid-cols-2 gap-8">
        {/* Input Panel */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          className="space-y-6"
        >
          <h2 className="text-2xl font-bold text-slate-100">Upload Image</h2>

          {/* File Upload Area */}
          <div 
            className="border-2 border-dashed border-slate-600 rounded-2xl p-8 text-center hover:border-cyan-500 transition-colors cursor-pointer"
            onClick={() => fileInputRef.current?.click()}
          >
            <i className="fas fa-cloud-upload-alt text-4xl text-slate-400 mb-4"></i>
            <p className="text-lg mb-2 text-slate-300">Drag and drop an image here</p>
            <p className="text-slate-400 mb-4">or click to browse</p>
            <p className="text-xs text-slate-500 mb-4">Supports JPG, PNG, WebP formats</p>
            <button className="bg-cyan-600 hover:bg-cyan-700 text-white px-6 py-3 rounded-xl transition-colors">
              <i className="fas fa-folder-open mr-2"></i>
              Choose Image
            </button>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="hidden"
              data-testid="input-file-upload"
            />
          </div>

          {/* Original Image Preview */}
          {originalImage && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="glassmorphism p-6 rounded-xl"
            >
              <h3 className="text-lg font-semibold text-slate-100 mb-4">Original Image</h3>
              <div className="aspect-video bg-slate-800 rounded-lg overflow-hidden mb-4">
                <img
                  src={originalImage.dataUrl}
                  alt="Original"
                  className="w-full h-full object-contain"
                  data-testid="image-original-preview"
                />
              </div>
              <div className="flex justify-between text-sm text-slate-400">
                <span>{originalImage.name}</span>
                <span>{(originalImage.size / 1024 / 1024).toFixed(2)} MB</span>
              </div>
            </motion.div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={removeBackground}
              disabled={!originalImage || isProcessing}
              className="flex-1 bg-cyan-600 hover:bg-cyan-700 disabled:bg-slate-700 disabled:cursor-not-allowed text-white py-4 rounded-2xl text-lg font-semibold transition-colors"
              data-testid="button-remove-background"
            >
              {isProcessing ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Processing...
                </>
              ) : (
                <>
                  <i className="fas fa-magic mr-2"></i>
                  Remove Background
                </>
              )}
            </button>
            <button
              onClick={resetTool}
              className="px-6 py-4 glassmorphism hover:bg-slate-700/50 text-slate-300 rounded-2xl transition-colors"
              data-testid="button-reset"
            >
              <i className="fas fa-redo mr-2"></i>
              Reset
            </button>
          </div>

          {/* Info Box */}
          <div className="glassmorphism p-6 rounded-xl">
            <h3 className="text-lg font-semibold text-slate-100 mb-3">
              <i className="fas fa-info-circle text-cyan-400 mr-2"></i>
              How it Works
            </h3>
            <div className="text-sm text-slate-400 space-y-2">
              <p>â€¢ This tool uses advanced algorithms to detect and remove backgrounds</p>
              <p>â€¢ Works best with clear subject-background contrast</p>
              <p>â€¢ All processing happens in your browser - your images stay private</p>
              <p>â€¢ Supports common image formats (JPG, PNG, WebP)</p>
            </div>
          </div>
        </motion.div>

        {/* Output Panel */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="space-y-6"
        >
          <h2 className="text-2xl font-bold text-slate-100">Result</h2>

          {/* Preview Area */}
          <div className="glassmorphism rounded-2xl p-6 h-96">
            {processedImage ? (
              <div className="h-full">
                <h3 className="text-lg font-semibold text-slate-100 mb-4">Background Removed</h3>
                <div className="aspect-video bg-transparent rounded-lg overflow-hidden mb-4" 
                     style={{backgroundImage: 'url("data:image/svg+xml;charset=UTF-8,%3csvg width=\'20\' height=\'20\' xmlns=\'http://www.w3.org/2000/svg\'%3e%3crect width=\'10\' height=\'10\' fill=\'%23374151\'/%3e%3crect x=\'10\' y=\'10\' width=\'10\' height=\'10\' fill=\'%23374151\'/%3e%3c/svg%3e")'}}>
                  <img
                    src={processedImage.dataUrl}
                    alt="Background removed"
                    className="w-full h-full object-contain"
                    data-testid="image-processed-preview"
                  />
                </div>
                <div className="flex justify-between items-center">
                  <div className="text-sm text-slate-400">
                    <div>{processedImage.name}</div>
                    <div>{(processedImage.size / 1024 / 1024).toFixed(2)} MB</div>
                  </div>
                  <button
                    onClick={downloadImage}
                    className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl transition-colors"
                    data-testid="button-download"
                  >
                    <i className="fas fa-download mr-2"></i>
                    Download
                  </button>
                </div>
              </div>
            ) : isProcessing ? (
              <div className="h-full flex items-center justify-center">
                <div className="text-center">
                  <i className="fas fa-spinner fa-spin text-6xl text-cyan-400 mb-4"></i>
                  <p className="text-slate-400">Removing background...</p>
                  <p className="text-sm text-slate-500 mt-2">This may take a few moments</p>
                </div>
              </div>
            ) : (
              <div className="h-full flex items-center justify-center">
                <div className="text-center text-slate-400">
                  <i className="fas fa-image text-6xl mb-4 opacity-50"></i>
                  <p>Processed image will appear here</p>
                  <p className="text-sm mt-2">Upload an image and click "Remove Background"</p>
                </div>
              </div>
            )}
          </div>

          {/* Processing Info */}
          <div className="glassmorphism rounded-2xl p-6">
            <h3 className="text-lg font-semibold mb-4 text-slate-100">Processing Details</h3>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-slate-400">Status:</span>
                <span className={`${isProcessing ? 'text-yellow-400' : processedImage ? 'text-green-400' : 'text-slate-400'}`}>
                  {isProcessing ? 'Processing...' : processedImage ? 'Complete' : 'Ready'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-slate-400">Output format:</span>
                <span className="text-slate-100">PNG (with transparency)</span>
              </div>
              <div className="flex justify-between">
                <span className="text-slate-400">Privacy:</span>
                <span className="text-green-400">100% local processing</span>
              </div>
            </div>
          </div>

          {/* Tips */}
          <div className="glassmorphism rounded-2xl p-6">
            <h3 className="text-lg font-semibold mb-4 text-slate-100">
              <i className="fas fa-lightbulb text-yellow-400 mr-2"></i>
              Tips for Best Results
            </h3>
            <div className="space-y-2 text-sm text-slate-400">
              <p>â€¢ Use images with clear contrast between subject and background</p>
              <p>â€¢ Avoid complex or busy backgrounds when possible</p>
              <p>â€¢ Higher resolution images typically produce better results</p>
              <p>â€¢ The tool works best with people, objects, and simple scenes</p>
            </div>
          </div>
        </motion.div>
      </div>
    </ToolShell>
  );
}

client/src/pages/tools/pdf/PDFMerger.jsx.content:
import { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import ToolShell from '../../../components/Tools/ToolShell';
import { getToolBySlug } from '../../../lib/toolsIndex';

export default function PDFMerger() {
  const tool = getToolBySlug('pdf', 'merge');
  const [files, setFiles] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [outputFile, setOutputFile] = useState(null);
  const fileInputRef = useRef(null);

  const handleFileSelect = (event) => {
    const selectedFiles = Array.from(event.target.files);
    const pdfFiles = selectedFiles.filter(file => file.type === 'application/pdf');

    const newFiles = pdfFiles.map((file, index) => ({
      id: Date.now() + index,
      file,
      name: file.name,
      size: file.size,
      preview: null
    }));

    setFiles(prev => [...prev, ...newFiles]);
  };

  const removeFile = (id) => {
    setFiles(prev => prev.filter(file => file.id !== id));
  };

  const moveFile = (fromIndex, toIndex) => {
    setFiles(prev => {
      const newFiles = [...prev];
      const [movedFile] = newFiles.splice(fromIndex, 1);
      newFiles.splice(toIndex, 0, movedFile);
      return newFiles;
    });
  };

  const mergePDFs = async () => {
    if (files.length < 2) {
      alert('Please select at least 2 PDF files to merge.');
      return;
    }

    setIsProcessing(true);

    try {
      // This would use PDF-lib or similar library for actual PDF merging
      // For demo purposes, we'll simulate the process
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Create a mock merged PDF blob
      const mockPDFContent = new Blob(['Mock merged PDF content'], { type: 'application/pdf' });
      setOutputFile({
        name: 'merged-document.pdf',
        blob: mockPDFContent,
        size: mockPDFContent.size
      });
    } catch (error) {
      console.error('Error merging PDFs:', error);
      alert('Error merging PDFs. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  const downloadMergedPDF = () => {
    if (!outputFile) return;

    const url = URL.createObjectURL(outputFile.blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = outputFile.name;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const resetTool = () => {
    setFiles([]);
    setOutputFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <ToolShell tool={tool} category="pdf">
      <div className="grid lg:grid-cols-2 gap-8">
        {/* Input Panel */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          className="space-y-6"
        >
          <h2 className="text-2xl font-bold text-slate-100">Upload PDF Files</h2>

          {/* File Upload Area */}
          <div 
            className="border-2 border-dashed border-slate-600 rounded-2xl p-8 text-center hover:border-cyan-500 transition-colors cursor-pointer"
            onClick={() => fileInputRef.current?.click()}
          >
            <i className="fas fa-cloud-upload-alt text-4xl text-slate-400 mb-4"></i>
            <p className="text-lg mb-2 text-slate-300">Drag and drop PDF files here</p>
            <p className="text-slate-400 mb-4">or click to browse</p>
            <button className="bg-cyan-600 hover:bg-cyan-700 text-white px-6 py-3 rounded-xl transition-colors">
              <i className="fas fa-folder-open mr-2"></i>
              Choose Files
            </button>
            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept=".pdf"
              onChange={handleFileSelect}
              className="hidden"
            />
          </div>

          {/* Files List */}
          {files.length > 0 && (
            <div className="space-y-3">
              <h3 className="text-lg font-semibold text-slate-100">Selected Files ({files.length})</h3>
              {files.map((file, index) => (
                <motion.div
                  key={file.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="glassmorphism p-4 rounded-xl flex items-center justify-between"
                >
                  <div className="flex items-center space-x-3">
                    <i className="fas fa-file-pdf text-red-400 text-xl"></i>
                    <div>
                      <div className="font-medium text-slate-100">{file.name}</div>
                      <div className="text-sm text-slate-400">
                        {(file.size / 1024 / 1024).toFixed(2)} MB
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => moveFile(index, Math.max(0, index - 1))}
                      disabled={index === 0}
                      className="p-2 text-slate-400 hover:text-white disabled:opacity-50 disabled:cursor-not-allowed"
                      title="Move up"
                    >
                      <i className="fas fa-arrow-up"></i>
                    </button>
                    <button
                      onClick={() => moveFile(index, Math.min(files.length - 1, index + 1))}
                      disabled={index === files.length - 1}
                      className="p-2 text-slate-400 hover:text-white disabled:opacity-50 disabled:cursor-not-allowed"
                      title="Move down"
                    >
                      <i className="fas fa-arrow-down"></i>
                    </button>
                    <button
                      onClick={() => removeFile(file.id)}
                      className="p-2 text-red-400 hover:text-red-300"
                      title="Remove file"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={mergePDFs}
              disabled={files.length < 2 || isProcessing}
              className="flex-1 bg-cyan-600 hover:bg-cyan-700 disabled:bg-slate-700 disabled:cursor-not-allowed text-white py-4 rounded-2xl text-lg font-semibold transition-colors"
            >
              {isProcessing ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Merging PDFs...
                </>
              ) : (
                <>
                  <i className="fas fa-magic mr-2"></i>
                  Merge PDFs
                </>
              )}
            </button>
            <button
              onClick={resetTool}
              className="px-6 py-4 glassmorphism hover:bg-slate-700/50 text-slate-300 rounded-2xl transition-colors"
            >
              <i className="fas fa-redo mr-2"></i>
              Reset
            </button>
          </div>
        </motion.div>

        {/* Output Panel */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="space-y-6"
        >
          <h2 className="text-2xl font-bold text-slate-100">Preview & Download</h2>

          {/* Preview Area */}
          <div className="glassmorphism rounded-2xl p-6 h-96 flex items-center justify-center">
            {outputFile ? (
              <div className="text-center">
                <i className="fas fa-file-pdf text-6xl text-green-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-slate-100 mb-2">PDF Merged Successfully!</h3>
                <p className="text-slate-400 mb-6">Your merged PDF is ready for download</p>
                <button
                  onClick={downloadMergedPDF}
                  className="bg-green-600 hover:bg-green-700 text-white px-8 py-3 rounded-xl transition-colors"
                >
                  <i className="fas fa-download mr-2"></i>
                  Download Merged PDF
                </button>
              </div>
            ) : isProcessing ? (
              <div className="text-center">
                <i className="fas fa-spinner fa-spin text-6xl text-cyan-400 mb-4"></i>
                <p className="text-slate-400">Processing your PDFs...</p>
              </div>
            ) : (
              <div className="text-center text-slate-400">
                <i className="fas fa-file-pdf text-6xl mb-4 opacity-50"></i>
                <p>Merged PDF preview will appear here</p>
                <p className="text-sm mt-2">Upload files and click merge to start</p>
              </div>
            )}
          </div>

          {/* File Information */}
          <div className="glassmorphism rounded-2xl p-6">
            <h3 className="text-lg font-semibold mb-4 text-slate-100">Merge Information</h3>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-slate-400">Total files:</span>
                <span className="text-slate-100">{files.length}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-slate-400">Total size:</span>
                <span className="text-slate-100">
                  {files.length > 0 
                    ? (files.reduce((total, file) => total + file.size, 0) / 1024 / 1024).toFixed(2) + ' MB'
                    : '0 MB'
                  }
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-slate-400">Processing time:</span>
                <span className="text-slate-100">
                  {isProcessing ? 'Processing...' : outputFile ? '< 2 seconds' : 'Not started'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-slate-400">Privacy:</span>
                <span className="text-green-400">100% local processing</span>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </ToolShell>
  );
}

