                        <SelectContent>
                          {Object.entries(aspectRatios).map(([key, ratio]) => (
                            <SelectItem key={key} value={key}>
                              {ratio.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </TabsContent>

                  <TabsContent value="freeform" className="space-y-4">
                    <div>
                      <Label>Custom Dimensions</Label>
                      <div className="grid grid-cols-2 gap-2 mt-2">
