  --cyan-400: hsl(187, 85%, 53%);
  --cyan-500: hsl(187, 85%, 48%);
  --purple-400: hsl(270, 95%, 75%);
  --purple-500: hsl(270, 91%, 65%);
  --purple-600: hsl(271, 81%, 56%);

  --red-400: hsl(0, 84%, 60%);
  --red-500: hsl(0, 72%, 51%);
  --green-400: hsl(142, 71%, 45%);
