  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(222, 84%, 5%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 text-slate-100 font-sans antialiased min-h-screen;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }

  .light body {
    @apply bg-gradient-to-br from-slate-50 via-slate-100 to-slate-50 text-slate-900;
  }
}

@layer components {
  .glassmorphism {
    background: rgba(30, 41, 59, 0.7);
    backdrop-filter: blur(12px);
    border: 1px solid rgba(148, 163, 184, 0.1);
  }

  .glassmorphism-dark {
    background: rgba(30, 41, 59, 0.3);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(148, 163, 184, 0.1);
  }

  .glass-nav {
    background: rgba(2, 6, 23, 0.8);
    backdrop-filter: blur(20px);
  }

  .gradient-text {
    background: linear-gradient(45deg, hsl(187, 85%, 53%), hsl(270, 95%, 75%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .tool-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .tool-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  }

  .hero-gradient {
    background: linear-gradient(135deg, 
      rgba(2, 6, 23, 1) 0%, 
      rgba(15, 23, 42, 0.95) 25%, 
      rgba(30, 41, 59, 0.9) 50%, 
      rgba(15, 23, 42, 0.95) 75%, 
      rgba(2, 6, 23, 1) 100%
    );
  }

  .ad-slot {
    min-height: 120px;
    background: rgba(30, 41, 59, 0.3);
    border: 2px dashed rgba(148, 163, 184, 0.3);
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: rgba(148, 163, 184, 0.6);
    font-size: 14px;
    text-align: center;
    margin: 2rem 0;
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.8s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.4s ease-out;
  }

  .animate-pulse-glow {
    animation: pulseGlow 2s infinite;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes slideUp {
  from { opacity: 0; transform: translateY(40px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes scaleIn {
  from { opacity: 0; transform: scale(0.9); }
  to { opacity: 1; transform: scale(1); }
}

@keyframes pulseGlow {
  0%, 100% { box-shadow: 0 0 20px rgba(34, 211, 238, 0.4); }
  50% { box-shadow: 0 0 40px rgba(34, 211, 238, 0.8); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(215, 28%, 17%);
}

::-webkit-scrollbar-thumb {
  background: hsl(187, 85%, 53%);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(187, 85%, 48%);
}

/* Accessibility improvements */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

.focus\:not-sr-only:focus {
  position: static;
  width: auto;
  height: auto;
  padding: inherit;
  margin: inherit;
  overflow: visible;
  clip: auto;
  white-space: normal;
}

/* Focus styles for accessibility */
.focus-visible:focus-visible {
  outline: 2px solid hsl(187, 85%, 53%);
  outline-offset: 2px;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .glassmorphism,
  .glassmorphism-dark {
    border: 2px solid currentColor;
    background: rgba(0, 0, 0, 0.9);
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .animate-fade-in,
  .animate-slide-up,
  .animate-scale-in,
  .animate-pulse-glow,
  .animate-float,
  .tool-card {
    animation: none;
    transition: none;
  }
}

